import { LOCATION_INITIALIZED } from "@angular/common";
import { TranslateHttpLoader } from "@ngx-translate/http-loader";
// AoT requires an exported function for factories
export function HttpLoaderFactory(httpClient) {
    return new TranslateHttpLoader(httpClient);
}
export function ApplicationInitializerFactory(translate, injector) {
    return async () => {
        await injector.get(LOCATION_INITIALIZED, Promise.resolve(null));
        const deaultLang = 'en';
        translate.addLangs(['en', 'ar']);
        translate.setDefaultLang(deaultLang);
        try {
            await translate.use(deaultLang).toPromise();
        }
        catch (err) {
            console.log(err);
        }
        console.log(`Successfully initialized ${deaultLang} language.`);
    };
}
